mapper.select:
SELECT * FROM course
WHERE 1=1

    AND name LIKE CONCAT('%', #{name}, '%')


    AND author LIKE CONCAT('%', #{author}, '%')


    AND user_id = #{userId}


    AND status = #{status}

ORDER BY create_time DESC
LIMIT #{offset}, #{size}

SELECT COUNT(*) FROM course
WHERE 1=1

    AND name LIKE CONCAT('%', #{name}, '%')


    AND author LIKE CONCAT('%', #{author}, '%')


    AND user_id = #{userId}


    AND status = #{status}
